package io.enkrypt.kafka.eth.utils

import io.enkrypt.avro.common.ContractType
import io.enkrypt.kafka.eth.utils.ERC20Tokens.BATByteCode
import io.enkrypt.kafka.eth.utils.ERC20Tokens.OmiseGoByteCode
import io.enkrypt.kafka.eth.utils.ERC20Tokens.WaltonTokenByteCode
import io.enkrypt.kafka.eth.utils.ERC721Tokens.CryptoKittiesByteCode
import io.enkrypt.kafka.eth.utils.ERC721Tokens.SuSquaresByteCode
import io.enkrypt.kafka.eth.utils.RandomByteCodes.EmptyByteCode
import io.enkrypt.kafka.eth.utils.RandomByteCodes.RandomByteCode1
import io.enkrypt.kafka.streams.utils.StandardTokenDetector
import io.enkrypt.kafka.streams.utils.TokenContractMatchResult
import io.kotlintest.shouldBe
import io.kotlintest.specs.ShouldSpec
import org.ethereum.util.ByteUtil

class StandardTokenDetectorTest : ShouldSpec(
  {

    should("detect compliant ERC20 tokens") {

      val erc20Tokens = listOf(
        OmiseGoByteCode,
        WaltonTokenByteCode,
        BATByteCode
      )

      erc20Tokens.forEach {
        StandardTokenDetector.detect(it) shouldBe Pair(ContractType.ERC20, TokenContractMatchResult.COMPLETE_MATCH)
      }

    }

    should("detect compliant ERC721 tokens") {

      val erc721Tokens = listOf(
        CryptoKittiesByteCode,
        SuSquaresByteCode
      )

      erc721Tokens.forEach {
        StandardTokenDetector.detect(it) shouldBe Pair(ContractType.ERC721, TokenContractMatchResult.COMPLETE_MATCH)
      }

    }

    should("not detect a non compliant token") {

      val randomByteCodes = listOf(
        RandomByteCode1,
        EmptyByteCode
      )

      randomByteCodes.forEach {
        StandardTokenDetector.detect(it) shouldBe Pair(ContractType.GENERIC, TokenContractMatchResult.NONE_MATCH)
      }

    }
  }
)

object ERC20Tokens {

  val OmiseGoByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

  val WaltonTokenByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

  val BATByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

}

object ERC721Tokens {

  val CryptoKittiesByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

  val SuSquaresByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

}

object RandomByteCodes {

  val RandomByteCode1: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

  val EmptyByteCode: ByteArray = ByteUtil.hexStringToBytes(
    ""
  )

}
